import react from "react"
import { Link } from "react-router-dom";
import './blockFilm.css';

const BlockFilm = (props) => {


return (
    <div>
        <div className="card">
            {props.data.map((el) => (
                el.map((item) => (
                    <>
                        <div className="card-header" key={item.episode_id}>
                            <h1>{item.title}</h1>
                            <h3>Year: {item.release_date}</h3>
                        </div>
                        <div className="card-body">
                            <h5 className="card-title">{item.opening_crawl}</h5>
                            <p className="card-text">{item.director}</p>
                                    <Link
                                    to={`/info/${item.episode_id}`}
                                    className="btn btn-primary" 
                                 >
                                    Перейти на фильм
                                </Link>
                        </div>
                    </>
                ))   
            ))}  
        </div>
    </div>
)

}

export default BlockFilm;
