import react from "react"
import { NavLink } from "react-router-dom";
import { Info } from "./info";

const BlockFilm = (props) => {

// console.log(props);
return (
        <div className="card">
            {props.data.map((el) => (
                el.map((item) => (
                    <>
                        <div className="card-header" key={item.episode_id}>
                            <h1>{item.title}</h1>
                            <h3>Year: {item.release_date}</h3>
                        </div>
                        <Routes>
                        <div className="card-body">
                            <h5 className="card-title">{item.opening_crawl}</h5>
                            <p className="card-text">{item.director}</p>
                            <NavLink 
                                to= "/info" 
                                className="btn btn-primary"
                            >
          <Route path={"/info"} element={<Info/>} />

                                Перейти на фильм
                            </NavLink>
                        </div>
                        </Routes>
                    </>
                ))   
            ))}  
        </div>
)

}

export default BlockFilm;
