import react, { useState } from "react"
import axios from "axios";
import { useEffect } from "react";
import { useParams } from "react-router";

const src = 'https://swapi.dev/api/films';

export const Info = (props) => {

const params = useParams();
const filmId = params.id;


 let [film, setFilm] = useState([])

    useEffect(()=> {
        axios
            .get(src)
            .then(data => {
                setFilm(data.data.results);
            })
    },[]);

let findFilm = film.find((item) => {
    if(item.episode_id == filmId){
        return true
    }
})


    return(
        <div class="card text-center">
            <div class="card-header">
                {findFilm.title}
            </div>
            <div class="card-body">
                <h5 class="card-title">{findFilm.o}</h5>
                <p class="card-text">С вспомогательным текстом ниже в качестве естественного перехода к дополнительному контенту.</p>
                <a href="#" class="btn btn-primary">Перейти куда-нибудь</a>
            </div>
            <div class="card-footer text-muted">
                2 дня спустя
            </div>
        </div>
    )
}
