import react, { useState } from "react"
import axios from "axios";
import { useEffect } from "react";
import { useParams } from "react-router";

const src = 'https://swapi.dev/api/films';

export const Info = () => {

const params = useParams();
const filmId = params.id;


 let [film, setFilm] = useState([])
 let [names, setNames] = useState()

    useEffect(()=> {
        axios
            .get(src)
            .then(data => {
                setFilm(data.data.results);
            })
    },[]);


let findFilm = film.find((item) => {
    if(item.episode_id == filmId){
        return true
    }
})


let add = []

add = findFilm?.characters || []

useEffect(() => {
    add.map((item) => {    
        axios.get(item).then(data => setNames(data.data)) 
    })
},names)

// console.log(names);


    return(
        <div class="card text-center">
            {film.length == 0 && <h1>loading</h1>}

            {film.length>0 &&

                <>
                    <div class="card-header">
                        <h2>{findFilm.title}</h2>
                        <h4>Year: {findFilm.release_date}</h4>
                    </div>
                    <div class="card-body">
                        <h5 class="card-title">{findFilm.opening_crawl}</h5>
                        <h4 class="card-text">Director: {findFilm.director}</h4>
                        <h4 class="card-text">Producer: {findFilm.producer}</h4>
                        {names == undefined && 
                          <h1>

                            </h1>                       
                        }   
                        {names !== undefined && 
                            <h1>{names.name}</h1>
                        }
                    </div>
                    <div class="card-footer text-muted">
                        2 дня спустя
                    </div>
                </>
            }
    </div>
    )
}
